mapper.select:
select *
from (
	select p.*, sum(paid_amount) over(order by paid_at desc) paid_sum
	from pay p
	where status != 'cancel' and advertiser_no = #{advertiserNo}
	order by paid_at desc
)
where paid_sum <= #{maxCancelAmount}

select rowtable.advertisement_no, advertiser_no, ad_name, ad_zone, ad_link, ad_cpc, ad_daily_budget, ad_status, ad_attach_no, original_filename, renamed_filename from ( select rownum rnum, a1.* from ( select a.*, dbms_random.random random from advertisement a where deleted_at is null and ad_status = 1
and ad_zone = #{adZone}
order by ad_cpc desc, random desc ) a1
where rownum between nvl(#{beforeAd}, 1) and #{topN}
) rowtable join ad_attach attach on rowtable.advertisement_no = attach.advertisement_no order by rnum
