mapper.select:
select count(0)
from used_product p, member m
where p.seller_id = m.member_id and p.cate_no=#{cateNo}

mapper.update:
update used_product
set product_views = product_views + 1
where product_no=#{no}

mapper.select:
select heart
from used_product
where product_no=#{productNo}

select *
from wish_product
where product_no=#{productNo} and member_id=#{memberId}
